import React, { Component } from 'react'
import stars from '../media/5_stars_logo.png'
export default class MainInfo extends Component {
    getArrPrices = () => {
        let prices = this.props.dataResponse.prices
    }
    render() {
        return (
            <div>
                <h1>{this.props.dataResponse.name}</h1>
                <small>
                    <span className = 'sku'>
                        SKU:
                        {this.props.dataResponse.partNumber}
                    </span>
                </small>
                <img src={stars}/>
                <span>(1 compActualizar)</span>
                <ul>
                    <li>Normal{this.props.prices.formattedOfferPrice}</li>
                    <li>Tarjeta Ripley {this.props.prices.formattedCardPrice}</li>
                    <li>Descuento {this.props.prices.formattedDiscount}</li>
                    <li>Con tarjeta Ripley acumulas <b>{this.props.prices.formattedDiscount}</b> RipleyPuntos GO </li>
                </ul>
                <a href ='#'></a>
            </div>
        )
    }
}
