import React, { Component } from 'react'
import logo from './logo.svg';
import './App.css';
import axios from 'axios'
import { render } from '@testing-library/react';
class App extends Component{
  state = {
    dataResponse : []
  }
  getArrImgsCarousel = dataResponse => {
    let arrImgsCarousel = dataResponse.images.map((image)=>{
      return image.value
    })
    return arrImgsCarousel
  }

  componentDidMount() {
    fetch('https://simple.ripley.cl/api/v2/products?format=json&partNumbers=2000379450763')
      .then((response) => response.json())
      .then((data) => this.setState({dataResponse:data[0]}))
  }
  render(){
    return (
      <div className="App">
        { this.dataResponse.name}
      </div>
    );
    }
}

export default App;
