import React, { Component } from 'react'
import logo from './logo.svg';
import './App.css';
import axios from 'axios'
import { render } from '@testing-library/react';
import MainInfo from './components/MainInfo'
class App extends Component{
  state = {
    dataResponse : []
  }
  getArrImgsCarousel = dataResponse => {
    let arrImgsCarousel = dataResponse.images.map((image)=>{
      return image.value
    })
    return arrImgsCarousel
  }

  componentDidMount() {
    fetch('http://simple.ripley.cl/api/v2/products?format=json&partNumbers=2000379450763',{mode: 'cors'})
      .then(res => res.json())
      .then(data => this.setState({dataResponse : data[0]}))
  }
  render(){
    return (
      <div className="App">
        <MainInfo
        dataResponse = {this.state.dataResponse}
        />
      </div>
    );
    }
}

export default App;
