import React, { Component } from 'react'
import logo from './logo.svg';
import './App.css';
import axios from 'axios'
import { render } from '@testing-library/react';
class App extends Component{
  state = {
    dataResponse : []
  }
  componentDidMount() {
    fetch('http://simple.ripley.cl/api/v2/products?format=json&partNumbers=2000379450763',{mode: 'cors'})
      .then(res => res.json())
      .then(data => this.setState({dataResponse : data[0]}))
  }
  render(){
    return (
      <div className="App">
        <header className="App-header">
          <img src={this.dataResponse.url} alt="img1" width="500" height="600"/>
        </header>
      </div>
    );
    }
}

export default App;
