import React, { Component } from 'react'
import stars from '../media/5_stars_logo.png'
export default class MainInfo extends Component {
    areRetiroEnTienda = () =>{
        if(!this.props.shipping.rTienda){
            return ' no '
        }else{
            return ' '
        }
    }
    checkStyles = () =>{
        if(!this.props.shipping.rTienda){
            return {color:'red'}
        }else{
            return {color:'green'}
        }
    }
    render() {
        return (
            <div className = 'item-description'>
                <h1>{this.props.dataResponse.name}</h1>
                <small>
                    <span className = 'sku'>
                        SKU{' '}:{' '}
                        {this.props.dataResponse.partNumber}
                    </span>
                </small>
                <img className = 'stars'src={stars} alt = "foto de estrellas"/>
                <ul>
                    <li>
                        <p>
                            Normal
                        </p>
                        <p>
                            {this.props.prices.formattedOfferPrice}
                        </p>
                    </li>
                    <li>
                        <p>
                        Tarjeta Ripley
                        </p>
                        <p>
                            {this.props.prices.formattedCardPrice}
                        </p>
                    </li>
                    <li>
                        <p>
                            Descuento
                        </p>
                        <p> 
                            {this.props.prices.formattedDiscount}
                        </p>
                    </li>
                </ul>
                <div>
                    Color
                    <a href ='#'>Negro</a>
                </div>
                <div className="shipping">
                    <p style = {this.checkStyles()}>
                        retiro en tienda{this.areRetiroEnTienda()}disponible
                    </p>
                </div>
            </div>
        )
    }
}
