import React, { Component } from 'react'
import Carous from 'react-elastic-carousel'
import ItemImage from './ItemImage'
export default class Carousel extends Component {
    render() {
        const settings = {
            dots: true,
            fade: true,
            infinite: true,
            speed: 200,
            slidesToShow: 1,
            arrows: true,
            slidesToScroll:1,
            classname: 'slides'
        }
        return (
            <Carous>
                {Object.keys(this.props.images).map((key)=>
                    <ItemImage
                    images = {this.props.images}
                    index = {key}
                    />
                )}
            </Carous>
        )
    }
}
