import React, { Component } from 'react'
import './styles/css/styles.css';
import MainInfo from './components/MainInfo';
import ItemImage from './components/ItemImage';
class App extends Component{
  state = {
    dataResponse : [],
    prices : [],
    images: [],
    shipping: []
  }
  getArrImgsCarousel = dataResponse => {
    let arrImgsCarousel = dataResponse.images.map((image)=>{
      return image.value
    })
    return arrImgsCarousel
  }
  componentDidMount() {
    fetch('https://simple.ripley.cl/api/v2/products?format=json&partNumbers=2000379450763',{method: 'get',mode:'cors',headers:{'Access-Control-Allow-Origin':'*'}})
      .then((response) => response.json())
      .then((data) => this.setState(
        {dataResponse: data[0],
          prices: data[0].prices,
          images: data[0].images,
          shipping: data[0].shipping
        }))
  }

  render(){
    fetch('https://simple.ripley.cl/api/v2/products?format=json&partNumbers=2000379450763',{method: 'get',mode:'cors',headers:{'Access-Control-Allow-Origin':'*'}})
      .then((response) => response.json())
      .then((data) => this.setState(
        {dataResponse: data[0],
          prices: data[0].prices,
          images: data[0].images,
          shipping: data[0].shipping
        }))
    return (
      <div className="App">
        <ItemImage
        images = {this.state.images}/>
        <MainInfo
        dataResponse={this.state.dataResponse}
        prices = {this.state.prices}
        shipping = {this.state.shipping}/>
      </div>
    );
    }
}

export default App;
